function crc16(str, crc = 0, xorout = 0) {
    for (let i = 0, t; i < str.length; i++, crc &= 0xFFFF) {
        t = (crc >> 8) ^ str.charCodeAt(i);
        t ^= t >> 4;
        crc = (crc << 8) ^ (t << 12) ^ (t << 5) ^ t;
    }
    return crc ^ xorout;
}

function buildVoltronicCommand(command, option = "") {
    const fullCommand = command + option;
    let commandHex = Buffer.from(fullCommand).toString('hex');
    let crcVal = crc16(fullCommand).toString(16);
    let messageHex = commandHex + crcVal + "0d";
    return Buffer.from(messageHex, "hex");
}

function parseVoltronicResponse(buffer) {
    if (!buffer || buffer.length < 3) return null;
    let response = buffer.toString('ascii').trim();
    response = response.replace(/[^\x20-\x7E]/g, '');
    if (response[0] === '(') response = response.substring(1);
    if (response.length > 4) response = response.substring(0, response.length - 4);
    return response.trim();
}

global.set("helpers", {
    crc16,
    buildVoltronicCommand,
    parseVoltronicResponse
});

node.status({ fill: "green", shape: "dot", text: "Helpers OK" });
return msg;

let opts;
try {
  opts = JSON.parse(msg.payload);
} catch (e) {
  node.error("options.json invalide: " + e.toString(), msg);
  node.status({ fill: "red", shape: "ring", text: "options.json invalide" });
  return null;
}

const serialPorts = (opts.serial_ports || [])
  .filter(p => typeof p === "string")
  .map(p => p.trim())
  .filter(p => p.length > 0);

// ‚úÖ s√©curit√©: ne pas √©craser une config existante avec une liste vide
if (serialPorts.length === 0) {
  const existing = global.get("config.serialPorts") || [];
  node.warn("Init: serial_ports vide -> on ne remplace pas la config existante (" + existing.length + " port(s))");
  node.status({ fill: "yellow", shape: "ring", text: `serial_ports vide (garde ${existing.length})` });
  msg.payload = { inverters: existing.length, serialPorts: existing, skipped: true };
  return msg;
}

const mqttConfig = {
  baseTopic: opts.mqtt_prefix || "voltronic",
  discoveryPrefix: "homeassistant",
  deviceName: "Onduleur Voltronic",
  broker: opts.mqtt_host || "core-mosquitto",
  port: opts.mqtt_port || 1883
};

const pollingFastMs = ((opts.polling_fast || opts.polling?.fast || 5) * 1000);

// ‚úÖ √©crire la config globale
global.set("config.serialPorts", serialPorts);
global.set("config.mqtt", mqttConfig);
global.set("config.pollingFastMs", pollingFastMs);

// ‚úÖ nettoyer d'anciens slots si le nombre de ports a diminu√©
const prevCount = global.get("config.invertersCount") || 0;
for (let i = serialPorts.length + 1; i <= prevCount; i++) {
  global.set(`inverter_${i}.port`, null);
  global.set(`inverter_${i}.data`, {});
  global.set(`inverter_${i}.lastUpdate`, 0);
}
global.set("config.invertersCount", serialPorts.length);

// ‚úÖ initialiser autant d'onduleurs que de ports
for (let i = 0; i < serialPorts.length; i++) {
  const inverterNum = i + 1;
  global.set(`inverter_${inverterNum}.port`, serialPorts[i]);
  if (!global.get(`inverter_${inverterNum}.data`)) global.set(`inverter_${inverterNum}.data`, {});
  global.set(`inverter_${inverterNum}.lastUpdate`, 0);
}

node.status({
  fill: "green",
  shape: "dot",
  text: `Config OK (${serialPorts.length} port(s))`
});

msg.payload = {
  inverters: serialPorts.length,
  serialPorts,
  mqtt: { broker: mqttConfig.broker, port: mqttConfig.port, baseTopic: mqttConfig.baseTopic },
  pollingFastMs
};
return msg;

const serialPorts = global.get("config.serialPorts") || [];
const messages = [];

for (let i = 0; i < serialPorts.length; i++) {
    const inverterNum = i + 1;
    messages.push({
        payload: "QPIGS",
        inverterNum: inverterNum,
        port: serialPorts[i],
        command: "QPIGS"
    });
}

return [messages];

const helpers = global.get("helpers");

if (!msg.command) {
    node.error("Pas de commande");
    return null;
}

const commandBuffer = helpers.buildVoltronicCommand(msg.command);

msg.payload = commandBuffer;

node.status({
    fill: "blue",
    shape: "dot",
    text: msg.command + " ‚Üí Onduleur " + msg.inverterNum
});

return msg;

const helpers = global.get("helpers");
const serialPorts = global.get("config.serialPorts") || [];

// --- D√©terminer le port r√©el de provenance ---
let port =
    msg.port ||
    msg.serialport ||
    (msg._session && msg._session.type === "serial" ? msg._session.serialport : null) ||
    msg.topic ||
    "";

// Normaliser (s√©curit√©)
if (typeof port !== "string") port = String(port || "");

// Si on n'a pas encore l'inverterNum, on le d√©duit du port
if (!msg.inverterNum) {
    const portIndex = serialPorts.findIndex(p => p === port);

    if (portIndex >= 0) {
        msg.inverterNum = portIndex + 1;
    } else {
        node.warn("Port non configur√©: " + (port || "<vide>"));
        node.warn("Champs dispo: port=" + (msg.port || "<none>") +
            " serialport=" + (msg.serialport || "<none>") +
            " topic=" + (msg.topic || "<none>"));
        return null;
    }
}

const inverterNum = msg.inverterNum;
const data = helpers.parseVoltronicResponse(msg.payload);

if (!data) return null;

const fields = data.split(" ");
if (fields.length < 20) return null;

const p = fields;

const parsed = {
    grid_voltage: parseFloat(p[0]) || 0,
    grid_frequency: parseFloat(p[1]) || 0,
    ac_output_voltage: parseFloat(p[2]) || 0,
    ac_output_frequency: parseFloat(p[3]) || 0,
    ac_output_apparent_power: parseInt(p[4]) || 0,
    ac_output_active_power: parseInt(p[5]) || 0,
    output_load_percent: parseInt(p[6]) || 0,
    bus_voltage: parseInt(p[7]) || 0,
    battery_voltage: parseFloat(p[8]) || 0,
    battery_charging_current: parseInt(p[9]) || 0,
    battery_capacity: parseInt(p[10]) || 0,
    inverter_heat_sink_temp: parseInt(p[11]) || 0,
    pv1_input_current: parseFloat(p[12]) || 0,
    pv1_input_voltage: parseFloat(p[13]) || 0,
    battery_voltage_scc: parseFloat(p[14]) || 0,
    battery_discharge_current: parseInt(p[15]) || 0,
    device_status: p[16] || "",
    pv1_charging_power: parseInt(p[19]) || 0
};

parsed.pv1_power = Math.round(parsed.pv1_input_voltage * parsed.pv1_input_current);
parsed.battery_power = Math.round(
    parsed.battery_voltage * (parsed.battery_charging_current - parsed.battery_discharge_current)
);

global.set("inverter_" + inverterNum + ".data", parsed);
global.set("inverter_" + inverterNum + ".lastUpdate", Date.now());

msg.payload = {
    inverterNum,
    command: "QPIGS",
    data: parsed,
    timestamp: new Date().toISOString()
};

node.status({ fill: "green", shape: "dot", text: "Onduleur " + inverterNum + ": OK" });

return msg;

const mqttConfig = global.get("config.mqtt");
const inverterNum = msg.payload.inverterNum;
const data = msg.payload.data;

const stateTopic = mqttConfig.baseTopic + "/" + inverterNum + "/state";

msg.topic = stateTopic;
msg.payload = JSON.stringify(data);
msg.retain = false;

return msg;

const mqttConfig = global.get("config.mqtt");
const serialPorts = global.get("config.serialPorts") || [];
const numInverters = serialPorts.length;
const messages = [];

for (let inverterNum = 1; inverterNum <= numInverters; inverterNum++) {
    const deviceId = "voltronic_" + inverterNum;
    const deviceName = "Onduleur Voltronic " + inverterNum;

    const device = {
        identifiers: [deviceId],
        name: deviceName,
        manufacturer: "Voltronic",
        model: "Ultra 11kW"
    };

    const sensors = [
        { key: "battery_voltage", name: "Tension Batterie", unit: "V", class: "voltage", icon: "mdi:battery" },
        { key: "battery_capacity", name: "SoC Batterie", unit: "%", class: "battery", icon: "mdi:battery-80" },
        { key: "battery_power", name: "Puissance Batterie", unit: "W", class: "power", icon: "mdi:battery-charging" },
        { key: "pv1_input_voltage", name: "Tension PV1", unit: "V", class: "voltage", icon: "mdi:solar-power" },
        { key: "pv1_input_current", name: "Courant PV1", unit: "A", class: "current", icon: "mdi:current-dc" },
        { key: "pv1_power", name: "Puissance PV1", unit: "W", class: "power", icon: "mdi:solar-power" },
        { key: "ac_output_voltage", name: "Tension Sortie AC", unit: "V", class: "voltage", icon: "mdi:power-plug" },
        { key: "ac_output_active_power", name: "Puissance Sortie AC", unit: "W", class: "power", icon: "mdi:lightning-bolt" },
        { key: "output_load_percent", name: "Charge Sortie", unit: "%", class: "power_factor", icon: "mdi:gauge" },
        { key: "inverter_heat_sink_temp", name: "Temp√©rature", unit: "¬∞C", class: "temperature", icon: "mdi:thermometer" }
    ];

    for (const sensor of sensors) {
        const config = {
            name: deviceName + " " + sensor.name,
            unique_id: deviceId + "_" + sensor.key,
            state_topic: mqttConfig.baseTopic + "/" + inverterNum + "/state",
            value_template: "{{ value_json." + sensor.key + " }}",
            unit_of_measurement: sensor.unit,
            device_class: sensor.class,
            icon: sensor.icon,
            device: device
        };

        messages.push({
            topic: mqttConfig.discoveryPrefix + "/sensor/" + deviceId + "/" + sensor.key + "/config",
            payload: JSON.stringify(config),
            retain: true
        });
    }
}

node.status({ fill: "green", shape: "dot", text: "Discovery: " + (numInverters * 10) + " capteurs cr√©√©s" });

return [messages];
